import React, { useEffect, useState } from 'react'
import { Table } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { Link } from 'react-router-dom';
import './App.css';

export default function StudentTable() {
const [students, setStudents]= useState('')

  useEffect(()=>{
    fetch('http://localhost:8000/students')
    .then((res)=>res.json())
    .then((data)=>{
      setStudents(data)
    }).catch((err)=>{
      console.log(err.message)
    })
  },[])

  const removeData = (id)=>{
    if(window.confirm("Are you sure to remove student data ?")){
      fetch("http://localhost:8000/students/"+id, {
        method: "DELETE",
      })
        .then((res) => {
          res.json();
          alert('Student data removed successfully');
          window.location.reload();
    })
    }
  }
  return (
    <div className="container mt-4">
      <Card className="col-md-5 mx-auto shadow" style={{ width: "80%" }}>
        <Card.Body>
          <Card.Title className="text-center align-content-center heading">Students List</Card.Title>
          <div className="d-flex justify-content-end">
            <Link className="btn btn-primary" to="/student/create/">
              Add Student
            </Link>
          </div>
<div>

</div>
          
        </Card.Body>
      </Card>
    </div>
  );
}
