import { useParams } from 'react-router-dom'
import React, { useEffect, useState } from 'react'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import './App.css';
import { Link } from 'react-router-dom';

export default function ViewStudent() {
  const { id } = useParams();
  const [studentData, setStudentData] = useState({})
  useEffect(()=>{
    fetch('http://localhost:8000/students/'+id)
    .then((res)=>res.json())
    .then((data)=>{
      
    }).catch((err)=>{
      console.log(err.message)
    })
  },[])
  return (
    <div className="container mt-4">
      <Card className="col-md-5 mx-auto shadow" style={{ width: "60%" }}>
        <Card.Body>
          <Card.Title className="text-center ">Student Details</Card.Title>
          { studentData && <div>
            <p><strong>Id : </strong>{studentData.id}</p>
            <p><strong>Name : </strong>{studentData.name}</p>
            <p><strong>Email : </strong>{studentData.email}</p>
            <p><strong>Phone : </strong>{studentData.phone}</p>
            <p><strong>Place : </strong>{studentData.place}</p>
          </div>}
          <div className="d-flex mt-4 justify-content-start">
          <Link className="btn btn-primary me-2" to={`/student/edit/${id}`}>
                Edit
              </Link>
              <Link className="btn btn-danger" to="/">
                Back
              </Link>
            </div>
        </Card.Body>
      </Card>
    </div>
  );
}
