import React, { useEffect, useState } from 'react'
import { Table } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { Link } from 'react-router-dom';
import './App.css';

export default function StudentTable() {
const [students, setStudents]= useState('')

  useEffect(()=>{
    fetch('http://localhost:8000/students')
    .then((res)=>res.json())
    .then((data)=>{
      setStudents(data)
    }).catch((err)=>{
      console.log(err.message)
    })
  },[])

  const removeData = (id)=>{
    if(window.confirm("Are you sure to remove student data ?")){
      fetch("http://localhost:8000/students/"+id, {
        method: "DELETE",
      })
        .then((res) => {
          res.json();
          alert('Student data removed successfully');
          window.location.reload();
    })
    }
  }
  return (
    <div className="container mt-4">
      <Card className="col-md-5 mx-auto shadow" style={{ width: "80%" }}>
        <Card.Body>
          <Card.Title className="text-center align-content-center heading">Students List</Card.Title>
          <div className="d-flex justify-content-end">
            <Link className="btn btn-primary" to="/student/create/">
              Add Student
            </Link>
          </div>
<div>
<Table className='overflow-x-scroll'>
            <thead>
              <tr className='ttt'>
                <th>Sr. No</th>
                <th>Name</th>
                <th>Email</th>
                <th>Phone</th>
                <th>Place</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {students &&
                students.map((item, index) => (
                  <tr key={item.id}>
                    <td>{index + 1}</td>
                    <td>{item.name}</td>
                    <td>{item.email}</td>
                    <td>{item.phone}</td>
                    <td>{item.place}</td>
                    <td>
                      <Link
                        className=" me-2"
                        to={`/student/view/${item.id}`}
                      >
                        <i className="fa fa-eye"></i> 
                      </Link>
                      <Link
                        className="me-2"
                        to={`/student/edit/${item.id}`}
                      >
                        <i className="fa fa-pencil text-warning"></i> 
                      </Link>
                      <Link
                        className=""
                        onClick={() => {
                          removeData(item.id);
                        }}
                      >
                        <i className="fa fa-trash text-danger"></i> 
                      </Link>
                    </td>
                  </tr>
                ))}
            </tbody>
          </Table>
</div>
          
        </Card.Body>
      </Card>
    </div>
  );
}
